/* 对axios进行二次封装 */
import axios from 'axios';
// import {message} from 'antd';

const ajax = (url, data={}, method='GET') => {
    return new Promise((resolve, reject) => {
        let p;
        if (method === 'GET') {
            p = axios.get(url, {
                params: data
            })
        } else if (method === 'POST') {
            p = axios.post(url, data)
        };
        p.then((response) => {
            resolve(response)
        }).catch((error) => {
            // message.error('请求出错了:' + error.message);
            reject(error);
        })
    })
    
}

export default ajax;
