/* 对axios进行二次封装 */
import axios from 'axios';

const ajax = (url, data={}, method='GET') => {
    if (method === 'GET') {
        return axios.get(url, {
            params: data
        })
    } else if (method === 'POST') {
        return axios.post(url, data)
    }
}

export default ajax;
